

public class AsignOneTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20,b=10;
		b=a-- - --a;
		System.out.println("b :"+b);
		
		int c=a--;
		System.out.println("c :"+c);
		
		int d=a>>2;
		System.out.println("d :"+d);
		
		int e=a&b;
		System.out.println("e :"+e);

	}

}
